import React from 'react';

const QuizQuestion = ({ question, options, handleAnswer, showFeedback, isCorrect, explanation, nextQuestion, currentQuestionIndex, totalQuestions }) => (
  <>
    <h2>確認テスト</h2>
    <p>{question}</p>
    {!showFeedback ? (
      options.map((option, index) => (
        <button key={index} onClick={() => handleAnswer(index)} className="quiz-option">
          {option}
        </button>
      ))
    ) : (
      <div className="feedback">
        <p>{isCorrect ? "正解です！" : "残念、不正解です。"}</p>
        <p>{explanation}</p>
        {currentQuestionIndex < totalQuestions - 1 ? (
          <button onClick={nextQuestion} className="next-button">次の問題へ</button>
        ) : (
          <button onClick={nextQuestion} className="next-button">結果を見る</button>
        )}
      </div>
    )}
  </>
);

export default QuizQuestion;
