import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import teacherIcon from './teacher.png';
import studentIcon from './student.png';
import marketing_1 from './images_1/marketeing_1.jpeg';
import marketing_2 from './images_1/marketeing_2.jpeg';

function App() {
  const [visibleStep, setVisibleStep] = useState(0);
  const chatContainerRef = useRef(null);

  const showNextStep = () => setVisibleStep(visibleStep + 1);

  const content = [
    {
      sender: "teacher",
      text: "こんにちは、今日はマーケティングについて学びましょう。",
    },
    {
      sender: "student",
      text: "こんにちは先生！マーケティングって何ですか？",
    },
    {
      sender: "teacher",
      text: "マーケティングは、商品やサービスを売り続けるための仕組みを作ることです。それには、顧客が「買いたい」と思うような気持ちを引き出すことが含まれます。",
    },
    {
      sender: "student",
      text: "売れ続ける仕組みってどうやって作るんですか？",
    },
    {
      sender: "teacher",
      text: "売れ続ける仕組みを作るには、いくつかのステップがあります。まず、ターゲットとする顧客層を明確にすることが大切です。その後、顧客のニーズや欲求を理解し、それに応える商品やサービスを提供します。",
    },
    {
      sender: "teacher",
      type: "image",
      src: marketing_1,
      alt: "売れ続ける仕組みづくり",
    },
    {
      sender: "student",
      text: "具体的にはどんなことをするんですか？",
    },
    {
      sender: "teacher",
      text: "例えば、市場調査を行って顧客の意見を集めたり、競合他社の動向を分析したりします。さらに、効果的な広告を作成して、顧客にリーチするためのチャネルを選びます。",
    },
    {
      sender: "student",
      text: " 買いたい気持ちづくりについて教えてください。",
    },
    {
      sender: "teacher",
      text: "買いたい気持ちを作るためには、顧客に対して商品の価値や魅力を伝えることが重要です。これには、広告やプロモーション、商品レビューなどが含まれます。また、顧客との信頼関係を築くことも大切です。",
    },
    {
      sender: "teacher",
      type: "image",
      src: marketing_2,
      alt: "買いたい気持ちづくり",
    },
    {
      sender: "student",
      text: "どんな方法で信頼関係を築くんですか？",
    },
    {
      sender: "teacher",
      text: "信頼関係を築くためには、顧客の期待に応えるサービスを提供することが基本です。例えば、迅速な対応や丁寧なカスタマーサポート、品質の高い商品を提供することです。さらに、顧客のフィードバックを大切にし、それに基づいてサービスを改善していくことも重要です。",
    },
    {
      sender: "student",
      text: "なるほど、他にも何か重要なポイントはありますか？",
    },
    {
      sender: "teacher",
      text: "はい、マーケティングは常に変化する市場や顧客のニーズに対応する柔軟性も重要です。常に情報を収集し、分析し、戦略を調整していくことで、長期的な成功を目指します。",
    }
  ];

  const getIcon = (sender) => {
    return sender === "teacher" ? teacherIcon : studentIcon;
  };

  useEffect(() => {
    if (chatContainerRef.current) {
      chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
    }
  }, [visibleStep]);

  return (
    <div className="App">
      <h1></h1>
      <div className="chat-container" ref={chatContainerRef}>
        {content.slice(0, visibleStep + 1).map((item, index) => (
          <div key={index} className={`chat-bubble ${item.sender}`}>
            <img src={getIcon(item.sender)} alt={`${item.sender} icon`} className="icon" />
            {item.type === "image" ? (
              <img src={item.src} alt={item.alt} className="image" />
            ) : (
              <p>{item.text}</p>
            )}
          </div>
        ))}
      </div>
      {visibleStep < content.length - 1 && (
        <button onClick={showNextStep} className="next-button">次へ</button>
      )}
    </div>
  );
}

export default App;
