import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, Link } from 'react-router-dom';
// import './App.css';
import Page1 from './components/Page1';
import LoginForm from './LoginForm';
import RegisterForm from './RegisterForm';
import apiRequest from './utils/apiRequest';

const isAuthenticated = () => !!localStorage.getItem('token');

const App = () => {
  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('refreshToken');
    window.location.href = '/marketing-app/login';
  };

  return (
    <div className="App">
      <Routes>
        <Route path="/marketing-app" element={<ProtectedRoute element={<HomePage onLogout={handleLogout} />} />} />
        <Route path="/marketing-app/Page1/:chapterId" element={<ProtectedRoute element={<Page1 />} />} />
        <Route path="/marketing-app/login" element={<LoginForm />} />
        <Route path="/marketing-app/register" element={<RegisterForm />} />
        <Route path="*" element={<Navigate to="/marketing-app" />} />
      </Routes>
    </div>
  );
};

const ProtectedRoute = ({ element }) => {
  return isAuthenticated() ? element : <Navigate to="/marketing-app/login" />;
};

const formatTime = (seconds) => {
  const h = Math.floor(seconds / 3600);
  const m = Math.floor((seconds % 3600) / 60);
  const s = seconds % 60;
  return `${h}時間 ${m}分 ${s}秒`;
};

const HomePage = ({ onLogout }) => {
  const [progress, setProgress] = useState([]);
  const [totalStudyTime, setTotalStudyTime] = useState(0);

  useEffect(() => {
    const fetchProgress = async () => {
      try {
        const response = await apiRequest('/api/progress/status');
        setProgress(response.data.progress);
        setTotalStudyTime(response.data.totalStudyTime);
      } catch (error) {
        console.error('Error fetching progress', error);
      }
    };

    fetchProgress();
  }, []);

  const chapters = Array.from({ length: 20 }, (_, i) => require(`./data/chapter1_${i + 1}.js`));
  
  return (
    <div className="home-container">
      <div className='progress-container'>
        <h2 className='progress-title'>学習の進捗状況</h2>
        <ul className='progress-list'>
          {progress.map((item, index) => (
            <li key={index} className='progress-item'>
              <div className='chapter-info'>
                <span className='chapter-id'>チャプター {item.chapterId}</span>
                <span className={`chapter-status ${item.completed ? 'status-completed' : 'status-incomplete'}`}>
                  {item.completed ? '完了' : '未完了'}
                </span>
              </div>
              <span className='study-time'>
                勉強時間: {formatTime(item.studyTime)}
              </span>
            </li>
          ))}
        </ul>
        <div className='total-study-time'>
          <span className='total-time-label'>総勉強時間:</span>
          <span className='total-time-value'>{formatTime(totalStudyTime)}</span>
        </div>
      </div>
      <div className="content-section">
        <h1 className="content-title">目次</h1>
        <nav className="content-navigation">
          <ul className="content-list">
            {chapters.map((chapter, i) => (
              <li className="content-item" key={i}>
                <Link to={`/marketing-app/Page1/${i + 1}`} className="content-link">
                  {chapter.title}
                </Link>
              </li>
            ))}
          </ul>
        </nav>      
      </div>
      <div>
        <button onClick={onLogout} className="logout-button">ログアウト</button>
      </div>
    </div>
  );
};

const AppWrapper = () => {
  return (
    <Router>
      <App />
    </Router>
  );
};

export default AppWrapper;
