import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import apiRequest from './utils/apiRequest'; // apiRequest を使用する場合
// import axios from 'axios';

const LoginForm = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      console.log('API Base URL:', process.env.REACT_APP_API_BASE_URL); // デバッグ用ログ

      // apiRequest を使用する場合
      const response = await apiRequest('/api/auth/login', {
        method: 'POST',
        data: { username, password }
      });

      // axios を直接使用する場合
      // const response = await axios.post(`${process.env.REACT_APP_API_BASE_URL}/api/auth/login`, { username, password });

      console.log('Login response:', response.data); // デバッグ用ログ
      localStorage.setItem('token', response.data.accessToken);
      localStorage.setItem('refreshToken', response.data.refreshToken);
      alert('Token stored successfully');
      navigate('/marketing-app');
    } catch (error) {
      console.error('Login error', error);
      setError('Login failed. Please try again.');
    }
  };

  return (
    <div>
      <h2>Login</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <form onSubmit={handleLogin}>
        <div>
          <label htmlFor="username">Username</label>
          <input
            type="text"
            id="username"
            name="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            name="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Login</button>
      </form>
      <p>Don't have an account? <Link to="/marketing-app/register">Register here</Link></p>
    </div>
  );
};

export default LoginForm;
