import React from 'react';

const QuizQuestion = ({ question, options, handleAnswer, showFeedback, isCorrect, explanation, nextQuestion, currentQuestionIndex, totalQuestions, score, completeChapter }) => (
  <div className="quiz-container">
    {currentQuestionIndex < totalQuestions ? (
      <>
        <h2>確認テスト</h2>
        <p>{question}</p>
        {!showFeedback ? (
          options.map((option, index) => (
            <button key={index} onClick={() => handleAnswer(index)} className="quiz-option">
              {option}
            </button>
          ))
        ) : (
          <div className="feedback">
            <p>{isCorrect ? "正解です！" : "残念、不正解です。"}</p>
            <p>{explanation}</p>
            {currentQuestionIndex < totalQuestions - 1 ? (
              <button onClick={nextQuestion} className="next-button">次の問題へ</button>
            ) : (
              <button onClick={() => setCurrentQuestionIndex(currentQuestionIndex + 1)} className="next-button">結果を見る</button>
            )}
          </div>
        )}
      </>
    ) : (
      <>
        <h2>テスト完了</h2>
        <p>あなたのスコア: {score}/{totalQuestions}</p>
        <p>お疲れ様でした！このテストを通じて、マーケティングの基本概念をより深く理解できたことを願っています。</p>
        <button onClick={completeChapter} className="next-button">チャプターを完了する</button>
      </>
    )}
  </div>
);

export default QuizQuestion;
