import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import apiRequest from './apiRequest';

const LoginForm = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [debug, setDebug] = useState(''); // デバッグメッセージ用のステートを追加
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      setDebug(`API Base URL: ${process.env.REACT_APP_API_BASE_URL}`); // デバッグ用メッセージ
      const response = await apiRequest('/api/auth/login', {
        method: 'POST',
        data: { username, password }
      });
      setDebug(`Login response: ${JSON.stringify(response.data)}`); // デバッグ用メッセージ
      // トークンをLocalStorageに保存
      localStorage.setItem('token', response.data.accessToken);
      localStorage.setItem('refreshToken', response.data.refreshToken);

      // トークンが正しく保存されているか確認
      setDebug(`Token: ${localStorage.getItem('token')}, Refresh Token: ${localStorage.getItem('refreshToken')}`);

      alert('Token stored successfully');
      navigate('/marketing-app');
    } catch (error) {
      console.error('Login error', error);
      setError('Login failed. Please try again.');
      setDebug(`Login error: ${error.message}`);
    }
  };

  return (
    <div>
      <h2>Login</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {debug && <p style={{ color: 'blue' }}>{debug}</p>} {/* デバッグメッセージを表示 */}
      <form onSubmit={handleLogin}>
        <div>
          <label htmlFor="username">Username</label>
          <input
            type="text"
            id="username"
            name="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            name="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Login</button>
      </form>
      <p>Don't have an account? <Link to="/marketing-app/register">Register here</Link></p>
    </div>
  );
};

export default LoginForm;
