import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate, useParams } from 'react-router-dom';
import apiRequest from '../utils/apiRequest';
import '../App.css';
import ChatBubble from '../components/ChatBubble';
import QuizQuestion from '../components/QuizQuestion';

// フックの前にチャプターデータをロード
const chapterData = Array.from({ length: 20 }, (_, i) => require(`../data/chapter1_${i + 1}.js`));

function Page1() {
  const { chapterId } = useParams();
  const chapterIndex = parseInt(chapterId, 10) - 1;

  // フックを先に呼び出す
  const [visibleStep, setVisibleStep] = useState(0);
  const [quizStarted, setQuizStarted] = useState(false);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [showFeedback, setShowFeedback] = useState(false);
  const [isCorrect, setIsCorrect] = useState(false);
  const chatContainerRef = useRef(null);
  const [studyTime, setStudyTime] = useState(0);
  const [startTime, setStartTime] = useState(Date.now());
  const navigate = useNavigate();

  // 無効なチャプターIDのチェックは早期リターン
  if (chapterIndex < 0 || chapterIndex >= chapterData.length) {
    return <div>Invalid Chapter ID</div>;
  }

  const { title, chapterOverview, content, quizQuestions } = chapterData[chapterIndex];

  const saveStudyTime = async (time) => {
    try {
      await apiRequest('/api/progress/update', {
        method: 'POST',
        data: {
          chapterId: `1_${chapterId}`,
          studyTime: time
        }
      });
    } catch (error) {
      console.error('Error saving study time', error);
    }
  };

  useEffect(() => {
    const handleVisibilityChange = () => {
      if (document.visibilityState === 'hidden' || document.hidden) {
        const endTime = Date.now();
        const elapsed = Math.floor((endTime - startTime) / 1000);
        setStudyTime(prevTime => {
          const newTime = prevTime + elapsed;
          saveStudyTime(newTime);
          return newTime;
        });
      } else {
        setStartTime(Date.now());
      }
    };

    const handleWindowFocus = () => {
      setStartTime(Date.now());
    };

    const handleWindowBlur = () => {
      const endTime = Date.now();
      const elapsed = Math.floor((endTime - startTime) / 1000);
      setStudyTime(prevTime => {
        const newTime = prevTime + elapsed;
        saveStudyTime(newTime);
        return newTime;
      });
    };

    document.addEventListener('visibilitychange', handleVisibilityChange);
    window.addEventListener('focus', handleWindowFocus);
    window.addEventListener('blur', handleWindowBlur);

    return () => {
      document.removeEventListener('visibilitychange', handleVisibilityChange);
      window.removeEventListener('focus', handleWindowFocus);
      window.removeEventListener('blur', handleWindowBlur);
      const endTime = Date.now();
      const elapsed = Math.floor((endTime - startTime) / 1000);
      saveStudyTime(studyTime + elapsed);
    };
  }, [startTime, studyTime]);

  const completeChapter = async () => {
    try {
      const endTime = Date.now();
      const elapsed = Math.floor((endTime - startTime) / 1000);
      const totalStudyTime = studyTime + elapsed;
      await saveStudyTime(totalStudyTime);

      await apiRequest('/api/progress/update', {
          method: 'POST',
          data: {
              chapterId: `1_${chapterId}`,
              completed: true,
              studyTime: totalStudyTime
          }
      });

      navigate('/');
    } catch (error) {
      console.error('Error completing chapter', error);
    }
  };

  const showNextStep = () => {
    if (visibleStep < content.length - 1) {
      setVisibleStep(visibleStep + 1);
    } else {
      setQuizStarted(true);
    }
  };

  const handleQuizAnswer = (selectedAnswer) => {
    const currentQuestion = quizQuestions[currentQuestionIndex];
    const correct = selectedAnswer === currentQuestion.correctAnswer;
    setIsCorrect(correct);
    setShowFeedback(true);
    if (correct) {
      setScore(score + 1);
    }
  };

  const nextQuestion = () => {
    setShowFeedback(false);
    if (currentQuestionIndex < quizQuestions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      completeChapter();
    }
  };

  useEffect(() => {
    if (chatContainerRef.current) {
      chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
    }
  }, [visibleStep]);

  useEffect(() => {
    const chatContainer = chatContainerRef.current;
    if (chatContainer) {
      const handleImageLoad = () => {
        chatContainer.scrollTop = chatContainer.scrollHeight;
      };

      chatContainer.addEventListener('load', handleImageLoad, true);

      return () => {
        chatContainer.removeEventListener('load', handleImageLoad, true);
      };
    }
  }, []);

  const progressPercentage = ((visibleStep + 1) / content.length) * 100;

  return (
    <div className="Content">
      <h1>{title}</h1>
      
      {visibleStep === 0 && (
        <div className="chapter-overview">
          <h2>チャプター概要</h2>
          <p>{chapterOverview}</p>
        </div>
      )}
      
      <div className="progress-bar-container">
        <div className="progress-bar" style={{ width: `${progressPercentage}%` }}></div>
      </div>
      
      {!quizStarted ? (
        <>
          <div className="chat-container" ref={chatContainerRef}>
            {content.slice(0, visibleStep + 1).map((item, index) => (
              item.type === "sectionTitle" ? (
                <h3 key={index} className="section-title">{item.text}</h3>
              ) : (
                <ChatBubble key={index} sender={item.sender} type={item.type} text={item.text} src={item.src} alt={item.alt} />
              )
            ))}
          </div>
          <button onClick={showNextStep} className="next-button">
            {visibleStep < content.length - 1 ? "次へ" : "確認テストを始める"}
          </button>
        </>
      ) : (
        <div className="quiz-container">
          {currentQuestionIndex < quizQuestions.length ? (
            <QuizQuestion
              question={quizQuestions[currentQuestionIndex].question}
              options={quizQuestions[currentQuestionIndex].options}
              handleAnswer={handleQuizAnswer}
              showFeedback={showFeedback}
              isCorrect={isCorrect}
              explanation={quizQuestions[currentQuestionIndex].explanation}
              nextQuestion={nextQuestion}
              currentQuestionIndex={currentQuestionIndex}
              totalQuestions={quizQuestions.length}
            />
          ) : (
            <>
              <h2>テスト完了</h2>
              <p>あなたのスコア: {score}/{quizQuestions.length}</p>
              <p>お疲れ様でした！このテストを通じて、マーケティングの基本概念をより深く理解できたことを願っています。</p>
              <button onClick={completeChapter} className="next-button">チャプターを完了する</button>
            </>
          )}
        </div>
      )}
      
      <div className="links-container">
        <Link to={`/marketing-app/Page1/${parseInt(chapterId) + 1}`}>次の章へ</Link>
        <Link to="/">ホームに戻る</Link>  
      </div>
    </div>
  );
}

export default Page1;
