import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_BASE_URL;

const refreshAccessToken = async (refreshToken) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/api/auth/token`, { token: refreshToken });
    localStorage.setItem('token', response.data.accessToken);
    return response.data.accessToken;
  } catch (error) {
    console.error('Error refreshing access token', error);
    alert('Error refreshing access token: ' + error.message);
    return null;
  }
};

const apiRequest = async (url, options = {}) => {
  let token = localStorage.getItem('token');
  const refreshToken = localStorage.getItem('refreshToken');

  if (!token) {
    alert('No token found. Redirecting to login.');
    window.location.href = '/marketing-app/login';
    return;
  }

  try {
    const response = await axios({
      url: `${API_BASE_URL}${url}`,
      ...options,
      headers: {
        ...options.headers,
        Authorization: `Bearer ${token}`
      }
    });
    return response;
  } catch (error) {
    console.error('API request error:', error);
    alert('API request error: ' + error.message);
    if (error.response && error.response.status === 403) {
      token = await refreshAccessToken(refreshToken);
      if (token) {
        try {
          const response = await axios({
            url: `${API_BASE_URL}${url}`,
            ...options,
            headers: {
              ...options.headers,
              Authorization: `Bearer ${token}`
            }
          });
          return response;
        } catch (retryError) {
          console.error('Retried API request error:', retryError);
          alert('Retried API request error: ' + retryError.message);
        }
      } else {
        window.location.href = '/marketing-app/login';
      }
    } else {
      throw error;
    }
  }
};

export default apiRequest;
